import React from 'react';
import CurrentWeather from '../components/currentWeather'
import './App.css';

class App extends React.Component{

  state = {
    current: '',
    daily: '',
    hourly: '',
  }

  currentInfo(){
    const now = new Date();
    const currentTime = `${now.getHours()}:${now.getMinutes() < 10 ? now.getMinutes() : "0"+now.getMinutes()}`
    const currentDate = `${now.getDay()}/${now.getMonth()+1}`
    return currentTime + " " + currentDate
  }

  componentDidMount(){
    fetch('http://localhost:3000/1.json')
      .then(response=> response.json())
      .then(item => {
        this.setState({current: item.current,daily: item.daily, hourly: item.hourly})
      })
  }

  render(){
    return (
      <div className="App">
        <p>Сейчас {this.currentInfo()}</p>
        <h3>Прогноз погоды на предстоящие 3 дня</h3>
        <CurrentWeather data={this.state.current}/>
      </div>
    );
  }
}

export default App;
