import React from 'react';
import CurrentWeather from '../components/currentWeather'
import hourlyWeather from '../components/hourlyWeather';

import './App.css';


class App extends React.Component{

  state = {
    current: '',
    daily: '',
    hourly: '',
    isLoading: false,
  }

  currentInfo(){
    const current = new Date()
    const currentTime = `${current.getHours()}:${current.getMinutes()}`
    const currentDate = `${current.getDate()}/${current.getMonth() + 1}`
    return currentTime + " " + currentDate
  }

  componentDidMount(){
    fetch('http://localhost:3000/1.json')
      .then(response=> response.json())
      .then(item => {
        this.setState({current: item.current,daily: item.daily, hourly: item.hourly, isLoading:true})
      })
  }

  render(){
    const {isLoading} = this.state;
    return (
      <div className="App">
        <p>Сейчас {this.currentInfo()}</p>
        <h3>Прогноз погоды в <input type="text" className="city_select"/></h3>
        {isLoading && <CurrentWeather data={this.state.current}/> }
        {isLoading && <hourlyWeather data={this.state.daily}/>}
      </div>
    );
  }
}

export default App;
