import React from 'react';
import CurrentWeather from '../components/currentWeather'
import HourlyWeather from '../components/hourlyWeather';
import DailyWeather from '../components/dailyweather';

import './App.css';


class App extends React.Component{

  state = {
    current: '',
    daily: '',
    hourly: '',
    isLoading: false,
  }

  currentInfo(){
    const current = new Date()
    const currentTime = `${current.getHours()}:${current.getMinutes()}`
    const currentDate = `${current.getDate()}/${current.getMonth() + 1}`
    return currentTime + " " + currentDate
  }

  componentDidMount(){
    fetch('https://api.openweathermap.org/data/2.5/onecall?lat=33.441792&lon=-94.037689&%20exclude=current,daily&units=metric&lang=ru&appid=2acbadd18c62985f6e7925919ab88df9')
      .then(response=> response.json())
      .then(item => {
        this.setState({current: item.current,daily: item.daily, hourly: item.hourly, isLoading:true})
      })
  }

  render(){
    const {isLoading} = this.state;
    return (
      <div className="App">
        <p>Сейчас {this.currentInfo()}</p>
        <h3>
          Прогноз погоды в \n
          <select className="city_select">
            <option value="59.939095 30.315868">Санкт-Петербурге</option>
            <option value="55.753215 37.622504">Москве</option>
            <option value="45.035470 38.975313">Краснодаре</option>
            <option value="55.159897 61.402554">Челябинск</option>
            <option value="48.480223, 135.071917">Хабаровск</option>
          </select>
        </h3>
        {isLoading && <CurrentWeather data={this.state.current}/> }
        {isLoading && <HourlyWeather data={this.state.hourly}/>}
        {isLoading && <DailyWeather data={this.state.daily}/>}
      </div>
    );
  }
}

export default App;
