import React from 'react';
import './App.css';

class App extends React.Component{

  currentInfo(){
    const now = new Date();
    const currentTime = `${now.getHours()} : ${now.getMinutes()}`
    const currentDate = `${now.getDay()}/${now.getMonth()+1}`
    return currentTime + " " + currentDate
  }

  componentDidMount(){
    fetch('http://localhost:3000/onecall.json').then(data=> JSON.stringify(data),() => {throw Error}).then(item=> console.log(item))
  }

  render(){
    return (
      <div className="App">
        <p>Сейчас {this.currentInfo()}</p>
        <h3>Прогноз погоды на предстоящие 3 дня</h3>
      </div>
    );
  }
}

export default App;
