import React from 'react';
import {timeConverter} from '../converter/converter'

const currentWeather = (props) => {
  const {data} = props;
  let info = data.weather;
  let array = {info}
  console.log(array.id)
  function fetchIcon(url){
    fetch(url).then(response => response)
  }
  return(
  <div>
      <p>Температура на улице {Math.round(data.temp)} &#8451;</p>
      <p>Ощущается как {Math.round(data.feels_like)}</p>
      <p>Облачность {data.clouds} %</p>
      <p>Влажность {data.humidity} %</p>
      <p>Давление {data.pressure}</p>
      <p>Восход {timeConverter(data.sunrise)}</p>
      <p>Закат {timeConverter(data.sunset)}</p>
      <p></p>
  </div>
  )
}

export default currentWeather;
