import React from 'react';
import CurrentWeather from '../components/currentWeather'
import './App.css';


class App extends React.Component{

  state = {
    current: '',
    daily: '',
    hourly: '',
  }

  currentInfo(){
    const current = new Date()
    const currentTime = `${current.getHours()}:${current.getMinutes()}`
    const currentDate = `${current.getDate()}/${current.getMonth() + 1}`
    return currentTime + " " + currentDate
  }

  componentDidMount(){
    fetch('http://localhost:3000/1.json')
      .then(response=> response.json())
      .then(item => {
        this.setState({current: item.current,daily: item.daily, hourly: item.hourly})
      })
  }

  render(){
    return (
      <div className="App">
        <p>Сейчас {this.currentInfo()} <input type="text" className="city_select"/></p>
        <h3>Прогноз погоды</h3>
        {Object.keys(this.state.current).length && <CurrentWeather data={this.state.current}/> }
      </div>
    );
  }
}

export default App;
