import React from 'react';
import './App.css';

function App() {
  const now = new Date();
  const currentTime = `${now.getHours()} : ${now.getMinutes()}`
  const currentDate = `${now.getDay()}/${now.getMonth()+1}`
  return (
    <div className="App">
      <p>Сейчас {currentDate} {currentTime}</p>
      <h3>Прогноз погоды на предстоящие 3 дня</h3>
    </div>
  );
}

export default App;
